function buscaCEP(r){var e="https://viacep.com.br/ws/"+r+"/json/";jQuery.ajax({url:e,method:"GET",dataType:"json",success:function(r){console.log(r),jQuery("#endereco").val(r.logradouro),jQuery("#bairro").val(r.bairro),jQuery("#cidade").val(r.localidade),jQuery("#estado").val(r.uf)},error:function(r,e,o){console.error("Erro:",o),jQuery("#resposta").text("Erro ao buscar o CEP.")}})}jQuery(document).ready(function(){jQuery("#cep").change(function(){buscaCEP(jQuery("#cep").val())})});