function gerarNumeroAleatorio(){return Math.floor(1e9*Math.random())}async function stringToBytes(r){return(new TextEncoder).encode(r)}async function generateSHA256Hash(r){const t=await stringToBytes(r),e=await crypto.subtle.digest("SHA-256",t);return Array.from(new Uint8Array(e)).map(r=>r.toString(16).padStart(2,"0")).join("")}async function gerarNumeroEChave(){const r=gerarNumeroAleatorio().toString(),t=await generateSHA256Hash(r);t.substring(0,20);window.localStorage.setItem("key",t)}gerarNumeroEChave();